"use strict";function initBuzzword(a){$("#buzzword").html(a.word),$("#buzzword-growth").html((a.growth/a.news_num*100).toFixed(1)+"%"),appendBuzzwordProvocativeList(a.provocativeRate),appendBuzzwordOutlinerList(a.isOutline)}function appendBuzzwordProvocativeList(a){$("#provocative-list").empty();for(var i in media)if(a[media[i]]>10){var n='<li class="hightlight"><span class="media-name">'+media[i]+'</span><span class="provocative-num">'+(100*a[media[i]]).toFixed(1)+"%</span></li>";$("#provocative-list").append(n)}for(var i in media)if(a[media[i]]<=10){var n='<li><span class="media-name">'+media[i]+'</span><span class="provocative-num">'+(100*a[media[i]]).toFixed(1)+"%</span></li>";$("#provocative-list").append(n)}}function appendBuzzwordOutlinerList(a){$("#outliner-list").empty();for(var i in media)if(1===a[media[i]]){var n='<li class="hightlight"><span class="media-name">'+media[i]+'</span><span class="news-num">大量報導</span></li>';$("#outliner-list").append(n)}for(var i in media)if(-1===a[media[i]]){var n='<li><span class="media-name">'+media[i]+'</span><span class="news-num">正常報導</span></li>';$("#outliner-list").append(n)}for(var i in media)if(0===a[media[i]]){var n='<li><span class="media-name">'+media[i]+'</span><span class="news-num">正常報導</span></li>';$("#outliner-list").append(n)}}